mapper.insert:
insert into notice() values((), #{noticeName}, sysdate, #{startDate}, #{endDate}, #{noticeContent}, #{announceDate}, #{empId}, 0,
#{noticeOriginalFileName}, #{noticeStoreFileName}, #{noticeFileSize}
null, null, null
)

mapper.select:
select *
from (select rownum rn, 
	from (select 
		from notice


				notice_no = #{noticeNo}



		order by notice_date desc


		order by notice_no asc

		))


		rn between #{startEndPageDTO.startRow} and #{startEndPageDTO.endRow}

mapper.update:
update notice
set read_count = read_count + 1
where notice_no = #{noticeNo}
