mapper.select:
select * from(select ROWNUM RN, RT_MENU, ODER_S_NUM, ORER_S_CHAR
			from(select RT_MENU, ODER_S_NUM, ORER_S_CHAR
		from order_statement o1, restaurant_menu m1
		
			o1.RT_MENU_NO = m1.RT_MENU_NO and RT_RV_NO = #{rtRvNo}
			
				ODER_S_NO = #{oderSno}
			
		
		))
		
			
				rn between #{startEndPageDTO.startRow} and #{startEndPageDTO.endRow}
