mapper.select:
select * from (select ROWNUM RN, 
			from(select 
		from restaurant_reservation


				RT_RV_NO = #{rtRvNo}


				RT_NO = #{rtNo}


		))


				rn between #{startEndPageDTO.startRow} and #{startEndPageDTO.endRow}

mapper.update:
update restaurant_reservation

	RT_RV_TNUM = #{rtRvTnum},
	RT_RV_NUM = #{rtrvNum},
	RT_RV_TIME = #{rtRvTime}


	RT_RV_NO = #{rtRvNo}
